INSERT INTO `academicos` (`idAcad`, `nomeAcad`, `cadeiraAcad`, `posicaoAcad`, `sobreAcad`, `fotoAcad`) VALUES
(35, 'Amarildo Clayton Godoi Brilhante', '16', 'Patrono: Érico Veríssimo.', 'Amarildo Brilhante é professor de Português da rede estadual e da rede particular de ensino, formado em Letras (UniToledo), Pedagogia (Jales), Direito (UniSalesiano), pós-graduado pela Universidade Federal de São Paulo (Unifesp), é técnico em Contabilidade, técnico em Informática. Foi coordenador de escola. Publicou seu livro “Amor, dom maior” em 2014, acadêmico da AAL desde 2017. ', 'Amarildo Clayton Godoi Brilhante0403202303031324.png'),
(36, 'Ana Lúcia de Arruda Ramos Rezende', '19', 'Patrono: Mário de Andrade.', 'Ana Lúcia de Arruda Ramos Rezende nasceu em Araçatuba. Psicopedagoga clínica e institucional, professora dedicada à infância. Livros publicados: Você é meu amigo? Festa na Floresta, Encontro na feira, Colorida, Tuco, Lenga-lenga do Tatu Bolinha. Está na Academia Araçatubense de Letras desde 2015. \r\n', 'Ana Lúcia de Arruda Ramos Rezende0503202309032709.jpg'),
(37, 'Antenor Rosalino', '1', 'Patrono: Olavo Bilac', 'Antenor Rosalino nasceu em Araçatuba. Aposentado pelo serviço público municipal. Livros publicados: Paisagens verbais, Prisma poético, Odisseia poética. Participou de várias coletâneas e venceu concursos literários. Pertence à Academia Araçatubense de Letras desde 2015. Poeta de estética parnasiana. ', 'Antenor Rosalino0503202309032755.jpg'),
(38, 'Antônio Luceni dos Santos', '15', 'Patrono: Carlos Aldrovandi.', 'Antonio Luceni é escritor, professor, jornalista, artista visual e arquiteto-urbanista. Doutorando em Artes pelo Instituto de Artes da Universidade Estadual Paulista - Unesp/ Câmpus São Paulo e Mestre em Letras/Estudos Literários pela Universidade Federal de Mato Grosso do Sul - UFMS/ Câmpus Três Lagoas. Possui as seguintes graduações: Letras/Português e Inglês, Artes Visuais e Arquitetura e Urbanismo. É especialista em Ensino do Texto; Educação, Intermeios e Artes Visuais e em Arquitetura e Cidade. É professor efetivo, por meio de concurso público, do Instituto Federal de Educação, Ciência e Tecnologia de São Paulo – IFSP, tendo atuado anteriormente nas redes públicas municipal e estadual e no sistema particular de ensino, nos diferentes níveis educacionais. Membro da Academia Araçatubense de Letras, ocupando a cadeira nº 15, e da União Brasileira de Escritores - UBE. Tem os seguintes livros publicados: “Júlia à procura da consciência perdida” (AAL); “Com quantos chapéus se faz um Chapeuzinho” (Escritório da Arte); “Poesia, pipoca e pião” (Somos); “Mosca Feliz” (Somos) e “O Menino e o Vento” (LetraSelvagem), entre outras participações na organização e publicação em coletâneas e períodos acadêmicos.', 'Antônio Luceni dos Santos1203202307034555.jpg'),
(39, 'Anizio Canola', '5', 'Patrono: Osmair Zanardi', 'Anizio Canola nasceu em Avaré, morou algum tempo em Paraguaçu Paulista onde foi repórter esportivo de campo, vindo a Araçatuba em 1975, por força de concurso para oficial de justiça. Continuou no rádio. Formou-se em Direito e publicou os seguintes livros: “Anízio Canola – Contos Poesia Crônicas” (2013) e uma coleção com dois volumes “A princesinha da alta Sorocabana (anos 60”, que narra sua experiência em Paraguaçu Paulista. Pertence à Academia Araçatubense de Letras desde 2014. \r\n', 'Anizio Canola0503202309032848.jpg'),
(40, 'Arnon Gomes dos Santos', '3', 'Patrono: Júlio César de Melo e Souza (Malba Tahan)', 'Arnon Gomes dos Santos é jornalista, escritor e professor. Nasceu em Santos, 1983. Em Araçatuba desde 2006. Presidente da Academia Araçatubense de Letras. Já publicou oito livros. Primeiro sobre o carnaval de Araçatuba (2008), os demais são biografias de personalidades como o jornalista José Hamilton Ribeiro, Genilson Senche e Napo. É biógrafo. Presidente da Academia Araçatubense de Letras por dois biênios. ', 'Arnon Gomes dos Santos0503202309033119.jpg'),
(41, 'Carlos Eduardo Brefore Pinheiro', '8', 'Patrono: Graciliano Ramos', 'Carlos Eduardo Brefore Pinheiro possui graduação em Letras, mestrado em Teoria da Literatura (UNESP), doutorado e pós-doutorado em Teoria Literária e Literatura Comparada (USP). Publicou os livros: “Entre o ínfimo e o grandioso, entre o passado e o presente: o jogo dialético da poética de Manoel de Barros” e “A tragédia do tédio da repetição em Clarice Lispector”. Coordenador e professor do Colégio Geração Raízes. É acadêmico da AAL desde 2017. Cadeira 8. Patrono: Graciliano Ramos. Presidente para o biênio 2023-24.', 'Carlos Eduardo Brefore Pinheiro0503202309032236.jpg'),
(42, 'Emília Goulart dos Santos', '13', 'Patrono: Afonso Arinosde Melo Franco.', 'Emília Goulart dos Santos nasceu em Buritama. Reside em Araçatuba desde de 1956. Possui curso de contabilidade. Membro do Grupo Experimental, do qual foi coordenadora, desde a sua fundação, 1999. Ganhadora de vários prêmios literários. Além de cronista, contista, é romancista, com livros à venda pela Amazon. Tem blog na internet. Tornou-se acadêmica em 2013.Livros publicados: Essa nossa gente (2004), O diário de vovó Lina (2011), Descaminhos dos anjos (2012), Excravos e rosas (2019-Amazon). ', 'Emília Goulart dos Santos0503202309033153.jpg'),
(43, 'Fernanda Colli', '14', 'Patrona: Janete Clair.', 'Fernanda Colli, escritora, pedagoga, engajada na cultura popular, especialmente na caipira, catireira, poetisa premiada, possui livros infantis publicados: “Um conto de fadas à moda caipira” 1, 2 e 3. Agente cultural. Toca vários instrumentos musicais. Escolhida em 2022 para ocupar uma cadeira na AAL. ', 'Fernanda Colli0403202303031251.jpg'),
(44, 'Fernando Henrique Bononi Verga', '4', 'Patrono: Almir Rodrigues Bento.', 'Fernando Henrique Bononi Verga nasceu em Braúna. Jornalista com pós-graduação (mestre) em comunicação midiática. Domina fluentemente o inglês. Músico. Foi secretário de Comunicação da Prefeitura de Araçatuba. Escreveu o livro “O alfaiate apressado e outros poemas”. Foi escolhido para a AAL em 2022. ', 'Fernando Henrique Bononi Verga0503202309033422.jpg'),
(45, 'Francisco Antônio Ferreira Tito Damazo', '17', 'Patrono: Carlos Drummond de Andrade.', 'Francisco Antônio Ferreira Tito Damazo é natural de Avanhandava. Tito Damazo é doutor em Letras (em literatura), autor de Insubmissão, Contrabaixo e Sob a batuta do bicho grilo – poesias; Ferreira Gullar: uma poética do sujo e “O canto do povo de um lugar”: uma leitura das canções de João do Vale – ensaios. Foi presidente da Academia Araçatubense por três biênios. ', 'Francisco Antônio Ferreira Tito Damazo0503202309033507.jpg'),
(46, 'Hélio Consolaro', '18', 'Patrono: Aldo Campos.', 'Hélio é natural de Araçatuba. Professor, jornalista, escritor e editor. Sete livros publicados. Foi vereador em Araçatuba e secretário municipal de Cultura por oito anos. Presidente da Academia Araçatubense por dois biênios. Publicou os seguintes livros: Cobras & Lagartos; Urubu Branco; Filósofo de Semáforo; Cerveja e uma porção de bobagem; Aulas de gramática aplicada; Casar! Contra quem?; Analógicos e digitais. Membro das academias de letras de Araçatuba- SP, Andradina-SP e Itaperuna-RJ. ', 'Hélio Consolaro0503202309033607.jpg'),
(47, 'Hosanah Spíndola Ataíde', '2', 'Patrono: D. Francisco Aquino Correia', 'Hosanah Spíndola Ataíde migrou para Araçatuba-SP aos 14 anos. Estudou depois de adulto. Vendedor de livros aposentado. Formou- se em Letras. Frequentador assíduo do Grupo Experimental da AAL. Escreveu dois livros: O Poeta do Cerrado e Minha Vida em Versos e Prosas. Participou de várias coletâneas. ', 'Hosanah Spíndola Ataíde0503202309033641.jpg'),
(48, 'José Hamilton Costa Brito', '7', 'Patrono: Vinicius de Morais.', 'José Hamilton Costa Brito é natural de Araçatuba. Cursos de letras e de Direito. Não exerceu nenhuma dessas profissões. Aposentou- se como representante comercial da indústria farmacêutica. Encontrou-se literariamente no Grupo Experimental da Academia Araçatubense de Letras do qual foi coordenador. Publicou “O que fazer”. ', 'José Hamilton Costa Brito0503202309033738.jpg'),
(49, 'Marcelo Henrique Teixeira Pinto', '11', 'Patrono: Fabriciano Juncal.', 'Marcelo Henrique Teixeira Pinto é jornalista UEL, já passou por vários canais de televisão, e foi secretário municipal de Comunicação da Prefeitura de Araçatuba. Atualmente possui uma agência “Melhor Notícia - Comunicação Estratégica”. Publicou três obras: “Incondicional” 40 anos de história da Associação de Amparo ao Excepcional Ritinha Prates. “Por onde ele me guia”, biografia de Francisco Matos Filho. “A fuga do soldado de borracha” – em parceria com Fernanda Franco. Eleito em 2022 para ocupar uma cadeira na AAL. ', 'Marcelo Henrique Teixeira Pinto0503202309033346.jpg'),
(50, 'Maria Aparecida de Godoy Baracat', '20', 'Patrono: Cora Coralina.', 'Maria Aparecida de Godoy Baracat nasceu em Macatuba. Concluiu o primeiro grau em Sorocaba. Mudou-se para Araçatuba em 1955, onde formou-se professora no antigo I.E. Manoel Bento da Cruz, onde se casou com Gilberto Baracat. Fez os cursos de letras, pedagogia e Direito. Publicou o livro “Ciranda de Vidro” (1998). Acha-se mais professora do que escritora. Declama muito bem seus poemas preferidos. ', 'Maria Aparecida de Godoy Baracat0503202309033818.jpg'),
(51, 'Marianice Paupitz Nucera', '9', 'Patrono: Paulo Setúbal.', 'Marianice Paupitz Nucera, Araçatuba, curso superior de letras, foi funcionária da Unesp e da Caixa Econômica Federal. Publicou De Retalho em Retalho, livro de contos e crônicas, possui textos em várias coletâneas. Venceu concurso de poemas da AAL como membro do Grupo Experimental, do qual foi coordenadora. Participa também do mundo teatral. ', 'Marianice Paupitz Nucera0503202309033917.jpg'),
(52, 'Marilurdes Martins Campezi', '10', 'Patrona: Cecília Meireles.', 'Marilurdes Martins Campezi. Nascida em Araçatuba. Professora aposentada de Língua Portuguesa. Fundadora da Academia Araçatubense de Letras. Publicou os seguintes livros: A arca da bicharada; A arca da bicharada – II; Porta retrato; Jogos do puxa- puxa, em parceria com Márcio Martins; Larissa e as estrelas; Tempo de colher margaridas. ', 'Marilurdes Martins Campezi0503202309033948.jpg'),
(53, 'Reynaldo Mauá Júnior', '6', 'Patrono: Euclides da Cunha.', 'Reynaldo Mauá Júnior – educador por opção; escritor por afeição. Venceslauense por nascimento; araçatubense por adoção. Os 70 já foram sem perdão. O tempo só faz estimular e provocar sua emoção. Livros: Planejamento Escolar e Entrecantos. ', 'Reynaldo Mauá Júnior0503202309034029.jpg'),
(54, 'Yara Clarice Pedro Rodrigues de Carvalho', '12', 'Patrono: Guilherme de Almeida.', 'Yara Clarice Pedro Rodrigues de Carvalho, professora aposentada, escritora, nascida em Araçatuba, pós-graduada em música, autora de mais de 10 livros infantis, já publicados: Um Dia, Um Anjo; Lanche de Bandeja; Capitão Melancia; Vida Marinha; O mistério do objeto luminoso. Gosta de pintura em tela. Ela é acadêmica da Academia Araçatubense de Letras. Foi sua presidente por um biênio. ', 'Yara Clarice Pedro Rodrigues de Carvalho0503202309034106.jpg');

