INSERT INTO `historia` (`idHis`, `texto`) VALUES
(3, 'Fundação da Academia Araçatubense de Letras e presidentes - Data: 25/11/1992\r\n\r\n1989/março – Curso de Letras das Faculdades Toledo inicia cadastro de escritores araçatubenses.\r\n1989/dezembro – tentativa de criação de uma associação de escritores ou de uma Academia Araçatubense de Letras, por Genilson Senche, então Secretário Municipal da Cultura.\r\n1990/janeiro – dado a público relatório final de cadastro de vinte nomes de escritores araçatubenses.\r\n1990/ junho – instalação do Conselho Municipal de Cultura.\r\n1992/junho – cultiva-se a idéia de criação de uma associação de escritores ou de uma academia de letras. Aprovação pelo Conselho Municipal de Cultura.\r\n1992/julho - forma-se a comissão pró Academia Araçatubense de Letras. Integrantes: representante do Conselho Municipal de Cultura e alguns escritores da cidade.\r\n1992/outubro – escolha, pela comissão, de quinze membros da futura Academia.\r\n1992 / novembro – Academia Araçatubense de Letras torna-se realidade com a assembleia de sua instalação.\r\n1 9 9 3 / m a r ç o – posse dos acadêmicos fundadores da Academia Araçatubense de Letras.\r\n\r\n');

